package M01N2;

public class M01N2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double numDouble = 3.1416;
		
		int numInt;
		float numFloat;
		String numString;
		
		numInt = (int)numDouble;
		numFloat = (float)numDouble;
		numString=Double.toString(numDouble);
		
		System.out.println(numDouble +"  Double");
		System.out.println(numInt+ "  int");
		System.out.println(numFloat+ "  float");
		System.out.println(numString+ "  String");
	}
	}


